mapper.select:
SELECT t.id, t.create_time, t.recoverytime, t.senderunit, t.receivingunit, t.receivenumber, t.sendernumber, t.sendline, t.filsavepath, t.receiptpath, t.Isreceipt, t.create_time, t.relate_type t.message FROM recycle_bin t WHERE 1 = 1
AND t.title LIEK CONCAT('%', #{title}, '%')
LIMIT ${start}, ${offset}

mapper.insert:
INSERT INTO recycle_bin(recoverytime,sendernumber, senderunit,receivingunit, receivenumber, filsavepath, create_time, receiptpath, Isreceipt,
 relate_type, sendline, message) VALUES (
  #{ recycleBin.recoverytime},#{ recycleBin.sendernumber}, #{recycleBin.senderunit}, #{recycleBin.receivingunit}, #{recycleBin.receivenumber}, #{recycleBin.filsavepath},
  #{recycleBin.create_time},#{recycleBin.receiptpath},#{recycleBin.Isreceipt},#{recycleBin.relate_type},#{recycleBin.sendline},#{recycleBin.message}
)
