mapper.select:
SELECT t.id, t.create_time, t.recoverytime, t.senderunit, t.receivingunit, t.receivenumber, t.sendnumber, t.sendline, t.filsavepath, t.receiptpath, t.Isreceipt, t.create_time, t.relate_type, t.message FROM recycle_bin t WHERE 1 = 1
AND t.senderunit LIKE CONCAT('%', #{params.senderunit}, '%')
AND t.receivingunit LIKE CONCAT('%', #{params.receivingunit}, '%')
AND t.receivenumber LIKE CONCAT('%', #{params.receivenumber}, '%')
AND t.sendnumber LIKE CONCAT('%', #{params.sendnumber}, '%')
AND t.sendline LIKE CONCAT('%', #{params.sendline}, '%')
and recoverytime >= #{params.recoveryBeginDate}
and recoverytime <= #{params.recoveryEndDate}
and create_time >= #{params.beginDate}
and create_time <= #{params.endDate}
LIMIT ${params.start}, ${params.offset}

SELECT
    COUNT(*)
FROM recycle_bin t
WHERE 1 = 1

    AND t.senderunit LIKE CONCAT('%', #{params.senderunit}, '%')


    AND t.receivingunit LIKE CONCAT('%', #{params.receivingunit}, '%')


    AND t.receivenumber LIKE CONCAT('%', #{params.receivenumber}, '%')


    AND t.sendnumber LIKE CONCAT('%',#{params.sendnumber},'%')


    AND t.sendline LIKE CONCAT('%', #{params.sendline}, '%')



    AND recoverytime >=  #{params.recoveryBeginDate}


    AND recoverytime <=  #{params.recoveryEndDate}



    AND create_time >=  #{params.beginDate}


    AND create_time <=  #{params.endDate}

mapper.insert:
INSERT INTO recycle_bin(recoverytime,sendnumber, senderunit,receivingunit, receivenumber, filsavepath, create_time, receiptpath, Isreceipt,
 relate_type, sendline, message) VALUES (
  #{ recycleBin.recoverytime},#{ recycleBin.sendnumber}, #{recycleBin.senderunit}, #{recycleBin.receivingunit}, #{recycleBin.receivenumber}, #{recycleBin.filsavepath},
  #{recycleBin.create_time},#{recycleBin.receiptpath},#{recycleBin.Isreceipt},#{recycleBin.relate_type},#{recycleBin.sendline},#{recycleBin.message}
)
